import React, { Fragment, useState } from 'react';

const Formulario = () => {

    // Crear State de Citas

    const [cita, actualizarCita] = useState({
        mascota: '',
        propietario: '',
        fecha: '',
        hora: '',
        sintomas: ''
    });

    const [ error, actualizarError] = useState(false)

    //Función que se ejecuta cuando el usuario escriba en un input

    const actualizarState = e => {
        //console.log(e.target.name); con esto me muestra el campo en el que se esta escribiendo
        //console.log(e.target.value); muestra el valor
        actualizarCita({
            ...cita,
            [e.target.name] : e.target.value
        })
    }

    //Extrar los valores
    const { mascota, propietario, fecha, hora, sintomas } =cita;

    //Cuando el usuario resioan agregar cita
    const submitCita = e => {
        e.preventDefault(); //previene la accion por default
        // se evita que lo mande por query string con el metodo get
        


        // Validar
        if(mascota.trim() === '' || propietario.trim() === '' || fecha.trim() === '' || hora.trim() === '' || sintomas.trim() === '') {
            console.log('esto va adentro del if')
            return;           
        }

        console.log('esto va fuera del if')

        //trim elimina espacios adelante y atras

        // Asignar un ID

        // Crear la cita

        //Reiniciar el form
    }

    return ( 
        <Fragment>    
            <h2>Crear Cita</h2>

            <form
                onSubmit={submitCita}
            >
                <label>Nombre mascota</label>
                <input
                    type="text"
                    name="mascota"
                    className="u-full-width"
                    placeholder="Nombre Mascota"
                    onChange={actualizarState}
                    value={mascota}
                />

                <label>Nombre Dueño</label>
                <input
                    type="text"
                    name="propietario"
                    className="u-full-width"
                    placeholder="Nombre Dueño de la mascota"
                    onChange={actualizarState}
                    value={propietario}
                />

                <label>Fecha</label>
                <input
                    type="date"
                    name="fecha"
                    className="u-full-width"
                    onChange={actualizarState}
                    value={fecha}
                />

                <label>Hora</label>
                <input
                    type="time"
                    name="hora"
                    className="u-full-width"
                    onChange={actualizarState}
                    value={hora}
                />

                <label>Síntomas</label>
                <textarea
                    className="u-full-width"
                    name="sintomas"
                    onChange={actualizarState}
                    value={sintomas}
                />

                <button
                    type="submit"
                    className="u-full-width button-primary"
                >Agregar Cita</button>

            </form>

        </Fragment>
    );
    
    
}

export default Formulario;
